import React, { useState, useEffect } from 'react';



const WatsonInput = () => {

  const [responce, setResponce] = useState( {watsonResponse: [], twitterHandle: "gwar", displayHandle: ''} )

  const callWatsonAPI = event => {
    event.preventDefault();
    fetch("http://localhost:9000/watsonAPI")
      .then(res => res.json())
      .then(res => setResponce({...responce, watsonResponse: res}))

      setResponce({...responce, displayHandle: responce.twitterHandle});
  }

  const getState = () => console.log(responce);

  useEffect(() => {
    callWatsonAPI();
    // this empty array functions to keep this from constatntly updating

    return () => {
      //runs function when component unmounts
    }
  }, []);

  return (
    <div>
      <button onClick={getState}>GET STATE</button>
    <h1>{responce.displayHandle}</h1>
      {/* <h1>{state.twitterHandle}</h1> */}
      <h5>{JSON.stringify(responce.watsonResponse)}</h5>
      <form onSubmit={callWatsonAPI}>
        <input
          type='text'
          id='twitterHandle'
          placeholder='Enter a Twitter handle'
          onChange={event => setResponce({...responce, twitterHandle: event.target.value})}          
        />
        <button type='submit'>Search</button>
      </form>
    </div>
  );




}

export default WatsonInput;
